resource.property:
select 
           cast ((select coalesce(total, 0.0) from wo_taxparts_total where ident = workorders.ident) * r_tax::numeric as numeric(7,2)) as saletax
         from workorders where ident = ::workorder:

select 
           cast ((select coalesce(total, 0.0) from wo_labor_total where ident = workorders.ident) * r_tax::numeric as numeric(7,2)) as saletax
         from workorders where ident = ::workorder:
